mapper.select:
/* QUERY: SiteMapper.selectList */
SELECT
    *
FROM
    sites
WHERE 1=1

        AND url = #{url}


        AND query = #{query}

mapper.insert:
/* QUERY: SiteMapper.insertOne */
INSERT INTO
    sites
    (
          url
        , header
        , form_body
        , query
        , latest_value
    )
VALUES
    (
          #{url}
        , #{header}
        , #{formBody}
        , #{query}
        , #{latestValue}
    )

mapper.delete:
/* QUERY: SiteMapper.delete */
DELETE FROM
    sites
WHERE 1=1
    AND id = #{id}
