mapper.insert:
/* SiteMapper.insertOne */
INSERT INTO
    sites
    (
          url
        , header
        , form_body
        , query
        , latest_value
    )
VALUES
    (
          #{url}
        , #{header}
        , #{formBody}
        , #{query}
        , #{latestValue}
    )

mapper.delete:
/* SiteMapper.delete */
DELETE FROM
    sites
WHERE 1=1
    AND id = #{id}
