mapper.insert:
/* SiteMapper.insertOne */
INSERT INTO
    site
    (
        idx

            , nm


            , type


            , url


            , query


            , lastval


            , reg_idx


            , chg_idx

    )
VALUES
    (
        #{idx}

            , #{nm}


            , #{type}


            , #{url}


            , #{query}


            , #{lastval}


            , #{regIdx}


            , #{chgIdx}

    )

mapper.delete:
/* SiteMapper.delete */
DELETE FROM
    site
WHERE 1=1
    AND id = #{id}
